import React, { Component } from 'react';
import { HeaderWrapper, Logo, Nav, NavItem, NavSearch, Addition, Button, SearchWapper } from './style';
import { CSSTransition } from 'react-transition-group';

class Header extends Component{

    constructor(props){
        super(props);
        this.state = {
            focused: false
        }
        this.handleInputFocus = this.handleInputFocus.bind(this);
        this.handleInputFocusBlur = this.handleInputFocusBlur.bind(this);
    }

    render(){
        return(
            <HeaderWrapper>
                <Logo />
                <Nav>
                    <NavItem className='left active'>
                        <i className="iconfont">&#xe61c;</i>
                        首页
                    </NavItem>
                    <NavItem className='left'>
                        <i className="iconfont">&#xe60f;</i>
                        下载APP
                    </NavItem>
                    <NavItem className='right'>登录</NavItem>
                    <NavItem className='right'>
                        <i className="iconfont">&#xe636;</i>
                    </NavItem>
                    <SearchWapper>
                        <CSSTransition
                            in={this.state.focused}
                            timeout={10000}
                            classNames="slide"
                        >
                            <NavSearch
                                className={!this.state.focused ? '': 'focused'}
                                onFocus={this.handleInputFocus}
                                onBlur={this.handleInputFocusBlur}
                            ></NavSearch>
                        </CSSTransition>
                    <i className={!this.state.focused ? 'iconfont': 'focused iconfont'}
                    >
                        &#xe631;
                    </i>
                    </SearchWapper>
                </Nav>
                <Addition>
                    <Button className='writting'>
                        <i className="iconfont">&#xe617;</i>
                        写文章
                    </Button>
                    <Button className='reg'>注册</Button>
                </Addition>
            </HeaderWrapper>
        );
    }
    handleInputFocus(){
    this.setState({
        focused: true
    })
    }
    handleInputFocusBlur(){
        this.setState(() =>{
            return{
                focused: false
            }
        });
    }
}

export default Header;
